import React, { useState } from "react";


const faqs = [
  {
    pregunta: "¿Este programa es solo para personas con diabetes tipo 1?",
    respuesta:
      "No. Está diseñado para personas con diabetes tipo 1, tipo 2 y prediabetes, con análisis de cada paciente y planes personalizados según cada caso.",
  },
  {
    pregunta: "¿Cuánto dura el programa?",
    respuesta:
      "El programa dura 6 meses con acompañamiento personalizado, pero podés ver beneficios desde las primeras semanas.",
  },
  {
    pregunta: "¿Es necesario tener experiencia en nutrición o entrenamiento?",
    respuesta:
      "No, está pensado para personas de cualquier nivel. Te acompañamos paso a paso.",
  },
];

const FaqCarrousel = () => {
  const [index, setIndex] = useState(0);

  const prev = () => {
    setIndex((prevIndex) => (prevIndex === 0 ? faqs.length - 1 : prevIndex - 1));
  };

  const next = () => {
    setIndex((prevIndex) => (prevIndex + 1) % faqs.length);
  };

  return (
    <div className="faq-carousel">
      <h3>Preguntas frecuentes (FAQ)</h3>
      <div className="faq-content">
        <button className="arrow left" onClick={prev}>
          ‹
        </button>
        <div className="faq-card">
          <p>{faqs[index].pregunta}</p>
          <p>{faqs[index].respuesta}</p>
        </div>
        <button className="arrow right" onClick={next}>
          ›
        </button>
      </div>
    </div>
  );
};

export default FaqCarrousel;
