import { useRef, useEffect } from "react";
import { register } from "swiper/element/bundle";

register();

export const FaqCarrousel = () => {
  const swiperElRef = useRef(null);

  useEffect(() => {
    swiperElRef.current.addEventListener("swiperprogress", (e) => {
      const [swiper, progress] = e.detail;
      console.log(progress);
    });

    swiperElRef.current.addEventListener("swiperslidechange", () => {
      console.log("slide changed");
    });
  }, []);

  return (
<swiper-container
  ref={swiperElRef}
  slides-per-view="3"
  space-between="30"
  centered-slides="true"
  navigation="true"
  initial-slide="1"
  autoplay="true"
  grab-cursor="true"   
>

      <swiper-slide>
        <div className="slide-text-container">
        <p className="slide-text1">¿Este programa es solo para personas con diabetes tipo 1?</p><br/>
        <p className="slide-text2">No. Está diseñado para personas con diabetes tipo 1,<br/>
        tipo 2 y prediabetes, con análisis de cada paciente<br/>
        y planes personalizados según cada caso.</p>
        </div>
      </swiper-slide>
      
      <swiper-slide>
        
      </swiper-slide>
    
      <swiper-slide>Slide 3</swiper-slide>
      
    </swiper-container>
  );
};


export default FaqCarrousel;
