import { useRef, useEffect } from 'react';
import { register } from 'swiper/element/bundle';
register();

export const FaqCarrousel = () => {
  const swiperElRef = useRef(null);

  useEffect(() => {
    // listen for Swiper events using addEventListener
    swiperElRef.current.addEventListener('swiperprogress', (e) => {
      const [swiper, progress] = e.detail;
      console.log(progress);
    });

    swiperElRef.current.addEventListener('swiperslidechange', (e) => {
      console.log('slide changed');
    });
  }, []);

  return (
    <swiper-container
      ref={swiperElRef}
      slides-per-view="1"
    
      pagination="true"
    >
      <swiper-slide>
        <p className="swiper-text">¿Este programa es solo para personas con diabetes tipo 1?</p><br />
        <p className="swiper-text">No. Está diseñado para personas con diabetes tipo 1,
        tipo 2 y prediabetes, con análisis de cada paciente
        y planes personalizados según cada caso.</p>
      </swiper-slide>

      <swiper-slide>Slide 2</swiper-slide>
      <swiper-slide>Slide 3</swiper-slide>
      ...
    </swiper-container>
  );
};

export default FaqCarrousel;
